import React from 'react';

const Hero = () => {
  return (
    <section id="home" className="container" style={styles.hero}>
      <div style={styles.content}>
        <h1 style={styles.title}>Smart Digital Solutions for Your Business</h1>
        <p style={styles.text}>
          We build high-performing websites and mobile apps that help your business scale faster in the digital world.
        </p>
        <button className="btn">Get Started</button>
      </div>
      <div style={styles.imageWrapper}>
        {/* Placeholder for the 3D developer illustration shown in PDF */}
        <div style={styles.placeholderImg}>
            <span style={{fontSize: '4rem'}}>ðŸ’»</span>
        </div>
      </div>
    </section>
  );
};

const styles = {
  hero: { display: 'flex', alignItems: 'center', minHeight: '80vh', gap: '50px' },
  content: { flex: 1 },
  title: { fontSize: '3.5rem', lineHeight: 1.2, color: '#2c3e50' },
  text: { fontSize: '1.1rem', color: '#666', marginBottom: '30px' },
  imageWrapper: { flex: 1, display: 'flex', justifyContent: 'center' },
  placeholderImg: { width: '100%', height: '400px', background: '#eefcf5', borderRadius: '20px', display: 'flex', alignItems: 'center', justifyContent: 'center' }
};

export default Hero;
