import React from 'react';
import { motion } from 'framer-motion';

const About = () => {
  return (
    <section id="aboutus" style={styles.section}>
      <div className="container" style={styles.grid}>
        <div style={styles.imageSide}>
           {/* PLACE IMAGE FROM PAGE 2 HERE */}
           <div style={styles.imagePlaceholder}>
             {/* <img src="DEVELOPER_IMAGE_URL" alt="Who We Are" /> */}
           </div>
        </div>

        <motion.div 
          initial={{ opacity: 0, x: 50 }}
          whileInView={{ opacity: 1, x: 0 }}
          style={styles.textSide}
        >
          <span className="section-subtitle">Who We Are [cite: 58]</span>
          <h2 style={styles.title}>A Software Development Company [cite: 59]</h2>
          <p style={styles.desc}>
            At Techverge Solutions, we craft powerful digital experiences. We specialize in custom websites, mobile apps, and scalable platforms[cite: 60].
          </p>
          <p style={styles.desc}>
            With a focus on clean design and smart development, our team turns complex ideas into seamless solutions[cite: 61].
          </p>
          <p style={styles.desc}>
            We collaborate with startups and enterprises worldwide to build technology that delivers real impact[cite: 62].
          </p>
        </motion.div>
      </div>
    </section>
  );
};

const styles = {
  section: { background: '#0a0a0a', padding: '120px 0' },
  grid: { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '80px', alignItems: 'center' },
  imageSide: { position: 'relative' },
  imagePlaceholder: { width: '100%', height: '450px', background: '#111', border: '1px solid #222', borderRadius: '30px' },
  textSide: { color: '#fff' },
  title: { fontSize: '3rem', margin: '20px 0' },
  desc: { color: '#888', marginBottom: '20px', fontSize: '1.1rem' }
};

export default About;
