import React from 'react';

const Testimonials = () => {
  return (
    <section className="container">
      <h2>Testimonials</h2>
      <p className="section-subtitle">What our clients say about us.</p>
      
      <div style={styles.card}>
        <div style={{ color: '#f1c40f', fontSize: '1.5rem', marginBottom: '15px' }}>★★★★★</div>
        <p style={styles.quote}>
          "Techverge Digital transformed our outdated system into a sleek, scalable web platform. 
          Their team was proactive, detail-oriented, and always responsive. We've seen a 40% increase 
          in operational efficiency since launch."
        </p>
        <div style={{ marginTop: '20px' }}>
          <h4 style={{ margin: 0 }}>Samantha Reeves</h4>
          [cite_start]<p style={{ color: '#666', fontSize: '0.9rem' }}>Operations Manager, Nexora</p>
        </div>
      </div>
    </section>
  );
};

const styles = {
  card: { maxWidth: '800px', margin: '0 auto', background: '#f4fbf7', padding: '50px', borderRadius: '15px', textAlign: 'center', borderLeft: '5px solid #2ecc71' },
  quote: { fontSize: '1.2rem', fontStyle: 'italic', color: '#333' }
};

export default Testimonials;
