import React, { useRef, useState } from 'react';
import { motion } from 'framer-motion';
import emailjs from '@emailjs/browser';

const ContactModal = ({ isOpen, onClose }) => {
  const form = useRef();
  const [status, setStatus] = useState("Send Inquiry");

  if (!isOpen) return null;

  const handleContactSubmit = (e) => {
    e.preventDefault();
    setStatus("Sending...");

    // Capture the current form
    emailjs.sendForm(
      "service_dr7gwfd", 
      "template_qnqsba6", 
      form.current, 
      "ckvfCOskvrB9_LAtF"
    )
    .then(() => {
      setStatus("Message Sent!");
      setTimeout(() => {
        setStatus("Send Inquiry");
        onClose();
      }, 2000);
    }, (error) => {
      console.error("EmailJS Error details:", error);
      setStatus("Error: Check Console");
    });
  };

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} style={styles.overlay} onClick={onClose}>
      <motion.div initial={{ y: 50 }} animate={{ y: 0 }} style={styles.modal} onClick={(e) => e.stopPropagation()}>
        <button onClick={onClose} style={styles.closeBtn}>Ã—</button>
        <div style={{ textAlign: 'center', marginBottom: '30px' }}>
          <h2 style={styles.title}>Contact <span style={{ color: '#2ecc71' }}>Us</span></h2>
        </div>
        
        <form ref={form} style={styles.form} onSubmit={handleContactSubmit}>
          <div style={styles.row}>
            {/* These 'name' attributes MUST match your {{tags}} */}
            <input name="from_name" type="text" placeholder="Full Name" style={styles.input} required />
            <input name="from_email" type="email" placeholder="Email Address" style={styles.input} required />
          </div>
          
          <select name="service_type" style={styles.input}>
            <option value="General Inquiry">General Inquiry</option>
            <option value="Web Development">Web Development</option>
            <option value="Mobile App">Mobile App</option>
          </select>
          
          <textarea name="message" placeholder="Message" style={{ ...styles.input, height: '120px' }} required />
          
          <button type="submit" style={styles.submitBtn}>{status}</button>
        </form>
      </motion.div>
    </motion.div>
  );
};

const styles = {
  overlay: { position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.9)', zIndex: 11000, display: 'flex', justifyContent: 'center', alignItems: 'center' },
  modal: { background: '#111', width: '90%', maxWidth: '600px', borderRadius: '30px', padding: '40px', border: '1px solid #222', position: 'relative' },
  closeBtn: { position: 'absolute', top: '15px', right: '20px', background: 'none', border: 'none', color: '#fff', fontSize: '2rem', cursor: 'pointer' },
  title: { fontSize: '2rem', color: '#fff' },
  form: { display: 'flex', flexDirection: 'column', gap: '15px' },
  row: { display: 'flex', gap: '15px' },
  input: { background: '#1a1a1a', border: '1px solid #333', padding: '15px', borderRadius: '10px', color: '#fff', outline: 'none' },
  submitBtn: { background: '#2ecc71', color: '#000', padding: '18px', borderRadius: '12px', fontWeight: 'bold', border: 'none', cursor: 'pointer' }
};

export default ContactModal;
