import React from 'react';

const Footer = ({ onContactClick }) => {
  return (
    <footer style={fStyles.footer}>
      <div className="container">
        <div style={fStyles.grid}>
          {/* Brand Side */}
          <div style={fStyles.brandSide}>
            <h2 style={fStyles.logo}>Techverge Solutions</h2>
            <p style={fStyles.brandText}>
              Building high-performing digital products for global clients.
            </p>
          </div>

          {/* Quick Links */}
          <div style={fStyles.linkGroup}>
            <h4 style={fStyles.linkHeading}>Company</h4>
            <a href="#home" style={fStyles.link}>Home</a>
            <a href="#aboutus" style={fStyles.link}>About Us</a>
            <a href="#projects" style={fStyles.link}>Projects</a> {/* Added Projects Link */}
            <a href="#services" style={fStyles.link}>Services</a>
          </div>

          {/* Contact Side */}
          <div style={fStyles.linkGroup}>
            <h4 style={fStyles.linkHeading}>Support</h4>
            <span onClick={onContactClick} style={fStyles.link}>Contact Us</span>
            <span style={fStyles.link}>Privacy Policy</span>
            <span style={fStyles.link}>Terms of Service</span>
          </div>
        </div>

        <div style={fStyles.bottomBar}>
          <p>Â© 2026 Techverge Solutions. All rights reserved.</p>
        </div>
      </div>
    </footer>
  );
};

const fStyles = {
  footer: { 
    background: '#080808', 
    padding: '80px 0 40px', 
    borderTop: '1px solid #111' 
  },
  grid: { 
    display: 'grid', 
    gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
    gap: '50px',
    marginBottom: '60px'
  },
  brandSide: { flex: 2 },
  logo: { color: '#fff', fontSize: '1.5rem', marginBottom: '20px', fontWeight: '900' },
  brandText: { color: '#666', maxWidth: '300px', lineHeight: '1.6' },
  linkGroup: { display: 'flex', flexDirection: 'column', gap: '12px' },
  linkHeading: { color: '#fff', marginBottom: '10px', fontSize: '1.1rem' },
  link: { 
    color: '#aaa', 
    textDecoration: 'none', 
    fontSize: '0.95rem', 
    cursor: 'pointer',
    transition: '0.3s'
  },
  bottomBar: { 
    borderTop: '1px solid #111', 
    paddingTop: '30px', 
    textAlign: 'center', 
    color: '#444',
    fontSize: '0.85rem' 
  }
};

export default Footer;
