import React from 'react';
import { motion } from 'framer-motion';
import { image } from 'framer-motion/client';

const About = () => {
  return (
    <section id="aboutus">
      <div className="container" style={styles.grid}>
        {/* Left Side: Image Space */}
        <div style={styles.imageSide}>
          <div style={styles.imgPlaceholder}>
            {image}
          </div>
        </div>

        {/* Right Side: Exact Text */}
        <motion.div initial={{ opacity: 0, x: 50 }} whileInView={{ opacity: 1, x: 0 }} style={styles.textSide}>
          <span style={{ color: '#2ecc71', fontWeight: 'bold' }}>Who We Are</span>
          <h2 style={styles.title}>A Software Development Company</h2>
          <p style={styles.p}>At Techverge Solutions, we craft powerful digital experiences. We specialize in custom websites, mobile apps, and scalable platforms[cite: 60].</p>
          <p style={styles.p}>With a focus on clean design and smart development, our team turns complex ideas into seamless solutions[cite: 61].</p>
          <p style={styles.p}>We collaborate with startups and enterprises worldwide to build technology that delivers real impact[cite: 62].</p>
        </motion.div>
      </div>
    </section>
  );
};

const styles = {
  grid: { display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '60px', alignItems: 'center' },
  imageSide: { background: '#111', height: '450px', borderRadius: '24px', border: '1px solid #222' },
  title: { fontSize: '3rem', margin: '20px 0', lineHeight: '1.2' },
  p: { color: '#888', fontSize: '1.1rem', marginBottom: '20px' }
};

export default About;
