// ... (Keep services array and imports the same)

const ServiceModal = ({ service, onClose }) => {
  const form = useRef();
  const [status, setStatus] = useState("Send Inquiry");

  const sendEmail = (e) => {
    e.preventDefault();
    setStatus("Sending...");

    // Replace these with your actual IDs from EmailJS Dashboard
    const SERVICE_ID = "YOUR_SERVICE_ID";
    const TEMPLATE_ID = "YOUR_TEMPLATE_ID";
    const PUBLIC_KEY = "YOUR_PUBLIC_KEY";

    emailjs.sendForm(SERVICE_ID, TEMPLATE_ID, form.current, PUBLIC_KEY)
      .then(() => {
        setStatus("Inquiry Sent!");
        setTimeout(() => onClose(), 2000);
      }, (error) => {
        console.error("FAILED...", error); // Check browser console (F12) for details
        setStatus("Failed to send");
      });
  };

  return (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} style={styles.modalOverlay} onClick={onClose}>
      <motion.div initial={{ scale: 0.8, y: 50 }} animate={{ scale: 1, y: 0 }} exit={{ scale: 0.8, y: 50 }} style={styles.modalContent} onClick={(e) => e.stopPropagation()}>
        <button onClick={onClose} style={styles.closeBtn}>Ã—</button>
        <h2 style={{ color: '#fff', marginBottom: '10px' }}>Inquire about</h2>
        <h3 style={{ color: '#2ecc71', marginBottom: '30px' }}>{service}</h3>
        
        <form ref={form} onSubmit={sendEmail} style={styles.form}>
          <input type="hidden" name="service_type" value={service} />
          <input type="text" name="from_name" placeholder="Your Name" required style={styles.input} />
          <input type="email" name="from_email" placeholder="Your Email" required style={styles.input} />
          <textarea name="message" rows="4" placeholder={`Tell us about your ${service} needs...`} required style={styles.input}></textarea>
          <button type="submit" className="btn hero-btn-primary" style={{ width: '100%', marginTop: '10px' }}>
            {status}
          </button>
        </form>
      </motion.div>
    </motion.div>
  );
};

