import React, { useState, useRef } from 'react';
import Tilt from 'react-parallax-tilt';
import { motion, AnimatePresence } from 'framer-motion';
import emailjs from '@emailjs/browser';

const services = [
  { title: "Web Development", icon: "</>", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "App Development", icon: "ðŸ“±", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "UI/UX Design", icon: "ðŸŽ¨", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "Product Strategy", icon: "ðŸ“ˆ", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "Custom Software", icon: "âš™ï¸", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "Post-Launch Support", icon: "ðŸ› ï¸", desc: "Innovative software solutions tailored to move your business forward." }
];

const Services = () => {
  const [selectedService, setSelectedService] = useState(null);

  const handleOpenForm = (serviceTitle) => {
    setSelectedService(serviceTitle);
    document.body.style.overflow = 'hidden'; 
  };

  const handleCloseForm = () => {
    setSelectedService(null);
    document.body.style.overflow = 'unset'; 
  };

  return (
    <section id="services" style={{ background: '#0a0a0a', position: 'relative' }}>
      <div className="container">
        <div style={{ textAlign: 'center', marginBottom: '60px' }}>
          <span className="section-subtitle">Our Expertise</span>
          <h2 style={{ fontSize: '3.5rem', color: '#fff', fontWeight: '900' }}>Our Services</h2>
          <p style={{ color: '#888' }}>Innovative software solutions tailored to move your business forward.</p>
        </div>
        
        <div style={styles.grid}>
          {services.map((s, i) => (
            <Tilt key={i} perspective={1000} glareEnable={true} glareMaxOpacity={0.1}>
              <motion.div 
                onClick={() => handleOpenForm(s.title)}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                whileHover={{ borderColor: '#2ecc71', cursor: 'pointer', scale: 1.02 }}
                transition={{ delay: i * 0.1 }}
                style={styles.card}
              >
                <div style={styles.icon}>{s.icon}</div>
                <h3 style={styles.cardTitle}>{s.title}</h3>
                <p style={styles.cardDesc}>{s.desc}</p>
              </motion.div>
            </Tilt>
          ))}
        </div>
      </div>

      <AnimatePresence>
        {selectedService && (
          <ServiceModal service={selectedService} onClose={handleCloseForm} />
        )}
      </AnimatePresence>
    </section>
  );
};

const ServiceModal = ({ service, onClose }) => {
  const form = useRef();
  const [status, setStatus] = useState("Send Inquiry");

  const sendEmail = (e) => {
    e.preventDefault();
    setStatus("Sending...");

    // Remember to replace these with your actual EmailJS keys from your dashboard
    const SERVICE_ID = "YOUR_SERVICE_ID";
    const TEMPLATE_ID = "YOUR_TEMPLATE_ID";
    const PUBLIC_KEY = "YOUR_PUBLIC_KEY";

    emailjs.sendForm(SERVICE_ID, TEMPLATE_ID, form.current, PUBLIC_KEY)
      .then(() => {
        setStatus("Inquiry Sent!");
        setTimeout(() => {
          onClose();
        }, 2000);
      }, (error) => {
        console.error(error.text);
        setStatus("Failed to send");
      });
  };

  return (
    <motion.div 
      initial={{ opacity: 0 }} 
      animate={{ opacity: 1 }} 
      exit={{ opacity: 0 }} 
      style={styles.modalOverlay}
      onClick={onClose}
    >
      <motion.div 
        initial={{ scale: 0.8, y: 50 }}
        animate={{ scale: 1, y: 0 }}
        exit={{ scale: 0.8, y: 50 }}
        style={styles.modalContent}
        onClick={(e) => e.stopPropagation()} 
      >
        <button onClick={onClose} style={styles.closeBtn}>Ã—</button>
        <h2 style={{ color: '#fff', marginBottom: '10px' }}>Inquire about</h2>
        <h3 style={{ color: '#2ecc71', marginBottom: '30px' }}>{service}</h3>
        
        <form ref={form} onSubmit={sendEmail} style={styles.form}>
          {/* Hidden input to capture which service they clicked on */}
          <input type="hidden" name="service_type" value={service} />
          
          <input type="text" name="from_name" placeholder="Your Name" required style={styles.input} />
          <input type="email" name="from_email" placeholder="Your Email" required style={styles.input} />
          <textarea name="message" rows="4" placeholder={`Tell us about your ${service} needs...`} required style={styles.input}></textarea>
          
          <button type="submit" className="btn hero-btn-primary" style={{ width: '100%', marginTop: '10px' }}>
            {status}
          </button>
        </form>
      </motion.div>
    </motion.div>
  );
};

const styles = {
  grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '30px' },
  card: { 
    background: '#111', 
    padding: '60px 40px', 
    borderRadius: '24px', 
    border: '1px solid #222', 
    textAlign: 'center', 
    transition: 'all 0.3s ease' 
  },
  icon: { fontSize: '3rem', color: '#2ecc71', marginBottom: '25px' },
  cardTitle: { color: '#fff', fontSize: '1.5rem', marginBottom: '15px', fontWeight: '700' },
  cardDesc: { color: '#777', fontSize: '1rem', lineHeight: '1.6' },
  
  modalOverlay: {
    position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
    background: 'rgba(0,0,0,0.85)', backdropFilter: 'blur(5px)', display: 'flex', 
    alignItems: 'center', justifyContent: 'center', zIndex: 9999, padding: '20px'
  },
  modalContent: {
    background: '#0f0f0f', padding: '50px', borderRadius: '30px',
    border: '1px solid #222', maxWidth: '500px', width: '100%', position: 'relative',
    boxShadow: '0 25px 50px -12px rgba(0, 0, 0, 0.5)'
  },
  closeBtn: {
    position: 'absolute', top: '20px', right: '20px', background: 'none',
    border: 'none', color: '#666', fontSize: '2rem', cursor: 'pointer'
  },
  form: { display: 'flex', flexDirection: 'column', gap: '15px' },
  input: {
    background: '#0a0a0a',
    border: '1px solid #333',
    padding: '15px',
    borderRadius: '10px',
    color: '#fff',
    outline: 'none',
    width: '100%'
  }
};

export default Services;
