import React, { useRef, useState } from 'react';
import { motion } from 'framer-motion';
import emailjs from '@emailjs/browser';

const ContactModal = ({ isOpen, onClose }) => {
  const form = useRef();
  const [status, setStatus] = useState("Send Inquiry");

  if (!isOpen) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    setStatus("Sending...");

    emailjs.sendForm("service_dr7gwfd", "template_qnqsba6", form.current, "ckvfCOskvrB9_LAtF")
      .then(() => {
        setStatus("Success!");
        setTimeout(() => { setStatus("Send Inquiry"); onClose(); }, 2000);
      }, (err) => {
        console.error(err);
        setStatus("Error: Check Console");
      });
  };

  return (
    <div style={styles.overlay} onClick={onClose}>
      <motion.div initial={{ y: 50, opacity: 0 }} animate={{ y: 0, opacity: 1 }} style={styles.modal} onClick={e => e.stopPropagation()}>
        <button onClick={onClose} style={styles.closeBtn}>Ã—</button>
        <h2 style={{ textAlign: 'center', marginBottom: '20px' }}>Let's Build <span style={{color: '#2ecc71'}}>Something Great</span></h2>
        
        <form ref={form} style={styles.form} onSubmit={handleSubmit}>
          <div style={styles.row}>
            <input name="from_name" type="text" placeholder="Your Name" style={styles.input} required />
            <input name="from_email" type="email" placeholder="Your Email" style={styles.input} required />
          </div>
          <select name="service_type" style={styles.input}>
            <option value="General Inquiry">General Inquiry</option>
            <option value="Web App">Web App</option>
            <option value="Mobile App">Mobile App</option>
          </select>
          <textarea name="message" placeholder="Project details..." style={{...styles.input, height: '120px'}} required />
          <button type="submit" style={styles.submitBtn}>{status}</button>
        </form>
      </motion.div>
    </div>
  );
};

const styles = {
  overlay: { position: 'fixed', inset: 0, background: 'rgba(0,0,0,0.95)', zIndex: 9999, display: 'flex', justifyContent: 'center', alignItems: 'center' },
  modal: { background: '#111', width: '90%', maxWidth: '550px', borderRadius: '25px', padding: '40px', border: '1px solid #222', position: 'relative' },
  closeBtn: { position: 'absolute', top: '15px', right: '20px', background: 'none', border: 'none', color: '#fff', fontSize: '2rem', cursor: 'pointer' },
  form: { display: 'flex', flexDirection: 'column', gap: '15px' },
  row: { display: 'flex', gap: '10px' },
  input: { background: '#000', border: '1px solid #333', padding: '15px', borderRadius: '10px', color: '#fff', outline: 'none' },
  submitBtn: { background: '#2ecc71', color: '#000', padding: '15px', borderRadius: '10px', fontWeight: 'bold', border: 'none', cursor: 'pointer' }
};

export default ContactModal;
