import React from 'react';
import { motion } from 'framer-motion';

const Hero = ({ onContactClick }) => {
  return (
    <section id="home" style={styles.hero}>
      <div className="container" style={styles.flex}>
        <motion.div 
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          transition={{ duration: 0.8 }}
          style={styles.textSide}
        >
          <span style={styles.overline}>TECHVERGE SOLUTIONS</span>
          <h1 style={styles.title}>
            Smart Digital <br/> 
            <span className="gradient-text">Solutions</span>
          </h1>
          <p style={styles.subtitle}>
            We build high-performing websites and mobile apps that help your business scale faster in the digital world.
          </p>
          <motion.button 
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={onContactClick}
            className="btn"
            style={styles.heroBtn}
          >
            Get Started Now
          </motion.button>
        </motion.div>

        <motion.div 
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 1 }}
          style={styles.imageSide}
        >
          {/* 3D Visual Element */}
          <div className="floating-blob" style={styles.blob}></div>
          <motion.div
            animate={{ y: [0, -20, 0] }}
            transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
            style={{ fontSize: '150px' }}
          >
            ðŸš€
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

const styles = {
  hero: { 
    minHeight: '100vh', 
    display: 'flex', 
    alignItems: 'center', 
    background: '#0a0a0a', /* Dark background matches site */
    paddingTop: '80px' /* Leave space for Navbar */
  },
  flex: { display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap', gap: '40px' },
  textSide: { flex: 1.2, minWidth: '300px' },
  overline: { color: '#2ecc71', fontWeight: 'bold', letterSpacing: '2px', fontSize: '0.8rem' },
  title: { fontSize: '4.5rem', fontWeight: '900', lineHeight: '1.1', color: '#fff', margin: '20px 0' },
  subtitle: { fontSize: '1.2rem', color: '#888', marginBottom: '40px', maxWidth: '500px' },
  heroBtn: { background: '#2ecc71', color: '#000', padding: '16px 40px', borderRadius: '50px', fontWeight: 'bold', fontSize: '1.1rem' },
  imageSide: { flex: 1, position: 'relative', textAlign: 'center' },
  blob: { position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: '300px', height: '300px', background: 'rgba(46, 204, 113, 0.15)', filter: 'blur(60px)', borderRadius: '50%', zIndex: -1 }
};

export default Hero;
