import React from 'react';

const Testimonials = () => {
  return (
    <section id="projects" style={{ background: '#0a0a0a' }}>
      <div className="container" style={{ textAlign: 'center' }}>
        <span style={{ color: '#2ecc71', fontWeight: 'bold' }}>TESTIMONIALS</span>
        <h2 style={{ fontSize: '3rem', margin: '10px 0 60px' }}>What our clients say about us [cite: 95]</h2>
        <div style={styles.card}>
          <div style={{ color: '#f1c40f', marginBottom: '20px' }}>★★★★★ [cite: 97]</div>
          <p style={styles.text}>
            "Techverge Digital transformed our outdated system into a sleek, scalable web platform. Their team was proactive, detail-oriented, and always responsive[cite: 98]."
          </p>
          <p style={{ color: '#2ecc71', marginBottom: '30px' }}>We've seen a 40% increase in operational efficiency since launch [cite: 99]</p>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '15px' }}>
            <div style={styles.avatar}>SR</div>
            <div style={{ textAlign: 'left' }}>
              <h4 style={{ margin: 0 }}>Samantha Reeves [cite: 100]</h4>
              <p style={{ margin: 0, color: '#666' }}>Operations Manager, Nexora [cite: 101]</p>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

const styles = {
  card: { background: '#111', padding: '60px', borderRadius: '40px', border: '1px solid #222', maxWidth: '800px', margin: '0 auto' },
  text: { fontSize: '1.5rem', fontStyle: 'italic', marginBottom: '20px' },
  avatar: { width: '50px', height: '50px', borderRadius: '50%', background: '#2ecc71', display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#000', fontWeight: 'bold' }
};

export default Testimonials;
