import React, { useState } from 'react';
import Tilt from 'react-parallax-tilt';
import { motion, AnimatePresence } from 'framer-motion';

const services = [
  { title: "Web Development", icon: "</>", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "App Development", icon: "ðŸ“±", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "UI/UX Design", icon: "ðŸŽ¨", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "Product Strategy", icon: "ðŸ“ˆ", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "Custom Software", icon: "âš™ï¸", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "Post-Launch Support", icon: "ðŸ› ï¸", desc: "Innovative software solutions tailored to move your business forward." }
];

const Services = () => {
  const [selectedService, setSelectedService] = useState(null);

  const handleOpenForm = (serviceTitle) => {
    setSelectedService(serviceTitle);
    document.body.style.overflow = 'hidden'; // Prevent scroll
  };

  const handleCloseForm = () => {
    setSelectedService(null);
    document.body.style.overflow = 'unset'; // Re-enable scroll
  };

  return (
    <section id="services" style={{ background: '#0a0a0a', position: 'relative' }}>
      <div className="container">
        <div style={{ textAlign: 'center', marginBottom: '60px' }}>
          <span className="section-subtitle">Our Expertise</span>
          <h2 style={{ fontSize: '3.5rem', color: '#fff', fontWeight: '900' }}>Our Services</h2>
          <p style={{ color: '#888' }}>Innovative software solutions tailored to move your business forward.</p>
        </div>
        
        <div style={styles.grid}>
          {services.map((s, i) => (
            <Tilt key={i} perspective={1000} glareEnable={true} glareMaxOpacity={0.1}>
              <motion.div 
                onClick={() => handleOpenForm(s.title)}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                whileHover={{ borderColor: '#2ecc71', cursor: 'pointer' }}
                transition={{ delay: i * 0.1 }}
                style={styles.card}
              >
                <div style={styles.icon}>{s.icon}</div>
                <h3 style={styles.cardTitle}>{s.title}</h3>
                <p style={styles.cardDesc}>{s.desc}</p>
                <div style={{ marginTop: '20px', color: '#2ecc71', fontWeight: 'bold', fontSize: '0.9rem' }}>
                  Inquire Now â†’
                </div>
              </motion.div>
            </Tilt>
          ))}
        </div>
      </div>

      {/* Service Specific Modal */}
      <AnimatePresence>
        {selectedService && (
          <ServiceModal service={selectedService} onClose={handleCloseForm} />
        )}
      </AnimatePresence>
    </section>
  );
};

// --- Internal Modal Component ---
const ServiceModal = ({ service, onClose }) => {
  return (
    <motion.div 
      initial={{ opacity: 0 }} 
      animate={{ opacity: 1 }} 
      exit={{ opacity: 0 }} 
      style={styles.modalOverlay}
      onClick={onClose}
    >
      <motion.div 
        initial={{ scale: 0.8, y: 50 }}
        animate={{ scale: 1, y: 0 }}
        exit={{ scale: 0.8, y: 50 }}
        style={styles.modalContent}
        onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside
      >
        <button onClick={onClose} style={styles.closeBtn}>Ã—</button>
        <h2 style={{ color: '#fff', marginBottom: '10px' }}>Inquire about</h2>
        <h3 style={{ color: '#2ecc71', marginBottom: '30px' }}>{service}</h3>
        
        <form style={styles.form}>
          <input type="text" placeholder="Your Name" required />
          <input type="email" placeholder="Your Email" required />
          <textarea rows="4" placeholder={`Tell us about your ${service} needs...`} required></textarea>
          <button type="submit" className="btn hero-btn-primary" style={{ width: '100%' }}>
            Send Inquiry
          </button>
        </form>
      </motion.div>
    </motion.div>
  );
};

const styles = {
  grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '30px' },
  card: { background: '#111', padding: '50px 30px', borderRadius: '24px', border: '1px solid #222', textAlign: 'center', transition: '0.3s' },
  icon: { fontSize: '2.5rem', color: '#2ecc71', marginBottom: '20px' },
  cardTitle: { color: '#fff', fontSize: '1.4rem', marginBottom: '15px' },
  cardDesc: { color: '#777', fontSize: '0.95rem' },
  
  // Modal Styles
  modalOverlay: {
    position: 'fixed', top: 0, left: 0, width: '100%', height: '100%',
    background: 'rgba(0,0,0,0.9)', display: 'flex', alignItems: 'center',
    justifyContent: 'center', zIndex: 9999, padding: '20px'
  },
  modalContent: {
    background: '#111', padding: '50px', borderRadius: '30px',
    border: '1px solid #222', maxWidth: '500px', width: '100%', position: 'relative'
  },
  closeBtn: {
    position: 'absolute', top: '20px', right: '20px', background: 'none',
    border: 'none', color: '#fff', fontSize: '2rem', cursor: 'pointer'
  },
  form: { display: 'flex', flexDirection: 'column', gap: '15px' }
};

export default Services;
