import React from 'react';
import { motion } from 'framer-motion';

const Hero = ({ onContactClick }) => {
  return (
    <section id="home" style={styles.hero}>
      <div className="container" style={styles.flex}>
        <motion.div initial={{ opacity: 0, x: -50 }} animate={{ opacity: 1, x: 0 }} style={styles.textSide}>
          <span style={styles.available}>Available for Work</span>
          <h1 style={styles.title}>
            Smart Digital <br/> 
            Solutions for Your <br/>
            <span className="gradient-text">Business</span>
          </h1>
          <p style={styles.subtitle}>
            We build high-performing websites and mobile apps that help your business scale faster in the digital world[cite: 55].
          </p>
          <div style={{ display: 'flex', gap: '15px' }}>
            <motion.button whileHover={{ scale: 1.05 }} onClick={onContactClick} className="btn" style={styles.mainBtn}>
              Contact us
            </motion.button>
          </div>
        </motion.div>

        <motion.div style={styles.imageSide}>
          <div className="floating-blob"></div>
          {/* Space for Page 2 Image */}
          <div style={styles.imageBox}>
             <span style={{fontSize: '150px'}}>ðŸš€</span>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

const styles = {
  hero: { minHeight: '100vh', display: 'flex', alignItems: 'center', paddingTop: '80px' },
  flex: { display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap' },
  textSide: { flex: 1.2, minWidth: '300px' },
  available: { color: '#2ecc71', fontWeight: 'bold', fontSize: '0.9rem', marginBottom: '15px', display: 'block' },
  title: { fontSize: '4.5rem', fontWeight: '900', lineHeight: '1.1', marginBottom: '25px' },
  subtitle: { fontSize: '1.1rem', color: '#888', marginBottom: '40px', maxWidth: '500px' },
  mainBtn: { background: '#2ecc71', color: '#000', padding: '16px 40px', borderRadius: '50px', fontWeight: 'bold' },
  imageSide: { flex: 1, position: 'relative', textAlign: 'center' },
  imageBox: { width: '100%', height: '400px', display: 'flex', alignItems: 'center', justifyContent: 'center' }
};

export default Hero;
