import React from 'react';
import Tilt from 'react-parallax-tilt';
import { motion } from 'framer-motion';

const services = [
  { title: "Web Development", icon: "</>", desc: "Innovative software solutions tailored to move your business forward. [cite: 75]" },
  { title: "App Development", icon: "ðŸ“±", desc: "Innovative software solutions tailored to move your business forward. [cite: 77]" },
  { title: "UI/UX Design", icon: "ðŸŽ¨", desc: "Innovative software solutions tailored to move your business forward. [cite: 79]" },
  { title: "Product Strategy", icon: "ðŸ“ˆ", desc: "Innovative software solutions tailored to move your business forward. [cite: 81]" },
  { title: "Custom Software", icon: "âš™ï¸", desc: "Innovative software solutions tailored to move your business forward. [cite: 83]" },
  { title: "Post-Launch Support", icon: "ðŸ› ï¸", desc: "Innovative software solutions tailored to move your business forward. [cite: 84]" }
];

const Services = () => {
  return (
    <section id="services" style={{ background: '#0a0a0a' }}>
      <div className="container">
        <div style={{ textAlign: 'center', marginBottom: '60px' }}>
          <h2 style={{ fontSize: '3rem', color: '#fff' }}>Our Services [cite: 72]</h2>
          <p style={{ color: '#888' }}>Innovative software solutions tailored to move your business forward[cite: 73].</p>
        </div>
        
        <div style={styles.grid}>
          {services.map((s, i) => (
            <Tilt key={i} perspective={1000} glareEnable={true} glareMaxOpacity={0.2}>
              <motion.div 
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ delay: i * 0.1 }}
                style={styles.card}
              >
                <div style={styles.icon}>{s.icon}</div>
                <h3 style={styles.cardTitle}>{s.title}</h3>
                <p style={styles.cardDesc}>{s.desc}</p>
              </motion.div>
            </Tilt>
          ))}
        </div>
      </div>
    </section>
  );
};

const styles = {
  grid: { display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '30px' },
  card: { background: '#111', padding: '50px 30px', borderRadius: '24px', border: '1px solid #222', textAlign: 'center' },
  icon: { fontSize: '2.5rem', color: '#2ecc71', marginBottom: '20px' },
  cardTitle: { color: '#fff', fontSize: '1.4rem', marginBottom: '15px' },
  cardDesc: { color: '#777', fontSize: '0.95rem' }
};

export default Services;
