import React from 'react';
import { motion } from 'framer-motion';

const projectList = [
  { title: "E-Commerce App", category: "Web Development" },
  { title: "Fintech Platform", category: "Software Solution" },
  { title: "Health Tracker", category: "Mobile App" }
];

// Animation Variants
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.2, // Time between each card appearing
    },
  },
};

const cardVariants = {
  hidden: { opacity: 0, y: 50, scale: 0.9 },
  visible: { 
    opacity: 1, 
    y: 0, 
    scale: 1,
    transition: { duration: 0.8, ease: "circOut" } 
  },
};

const Projects = () => {
  return (
    <section id="projects">
      <div className="container">
        <motion.div
          initial={{ opacity: 0, x: -20 }}
          whileInView={{ opacity: 1, x: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.8 }}
        >
          <span className="section-subtitle">Our Work</span>
          <h2 style={{ fontSize: '3.5rem', marginBottom: '50px', fontWeight: '900' }}>
            Featured Projects
          </h2>
        </motion.div>
        
        <motion.div 
          className="project-grid"
          variants={containerVariants}
          initial="hidden"
          whileInView="visible"
          viewport={{ once: true, amount: 0.2 }}
        >
          {projectList.map((project, index) => (
            <motion.div 
              key={index} 
              className="project-card"
              variants={cardVariants}
              whileHover={{ 
                y: -15, 
                scale: 1.02,
                transition: { duration: 0.3 }
              }}
            >
              <div className="project-image-placeholder">
                <motion.div 
                  className="project-overlay"
                  initial={{ opacity: 0.6 }}
                  whileHover={{ opacity: 1 }}
                >
                  <p>{project.category}</p>
                  <h3>{project.title}</h3>
                  
                  {/* Decorative line that grows on hover */}
                  <motion.div 
                    style={{ 
                      height: '2px', 
                      background: '#2ecc71', 
                      width: 0, 
                      marginTop: '10px' 
                    }}
                    whileHover={{ width: '100%' }}
                  />
                </motion.div>
              </div>
            </motion.div>
          ))}
        </motion.div>
      </div>
    </section>
  );
};

export default Projects;
