import React, { useState, useEffect } from 'react';
import { AnimatePresence } from 'framer-motion';
import emailjs from '@emailjs/browser';

// Component Imports
import Navbar from './components/Navbar';
import Hero from './components/Hero';
import About from './components/About';
import Services from './components/Services';
import Projects from './components/Projects';
import Stats from './components/Stats';
import Testimonials from './components/Testimonials';
import Footer from './components/Footer';
import Loader from './components/Loader';
import ContactModal from './components/ContactModal';

// Styles
import './App.css';

// Replace with your actual Public Key from EmailJS Account settings
emailjs.init("YOUR_PUBLIC_KEY"); 

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [isContactOpen, setIsContactOpen] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2500);
    return () => clearTimeout(timer);
  }, []);

  const toggleContact = () => {
    setIsContactOpen(!isContactOpen);
    // Prevents background scrolling when modal is open
    document.body.style.overflow = !isContactOpen ? 'hidden' : 'unset';
  };

  return (
    <div className="App">
      <AnimatePresence>
        {isContactOpen && (
          <ContactModal 
            key="contact-modal" 
            isOpen={isContactOpen} 
            onClose={toggleContact} 
          />
        )}
      </AnimatePresence>

      <AnimatePresence mode="wait">
        {isLoading ? (
          <Loader key="loader" />
        ) : (
          <div key="main-content">
            <Navbar onContactClick={toggleContact} />
            <main>
              <Hero onContactClick={toggleContact} />
              <section id="aboutus"><About /></section>
              <Services />
              <Projects />
              <Stats />
              <Testimonials />
            </main>
            <Footer onContactClick={toggleContact} />
          </div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
