import React from 'react';
import { motion } from 'framer-motion';

const Hero = ({ onContactClick }) => {
  return (
    <section id="home" style={styles.hero}>
      <div className="container" style={styles.flex}>
        <motion.div 
          initial={{ opacity: 0, x: -50 }}
          animate={{ opacity: 1, x: 0 }}
          style={styles.textSide}
        >
          <span style={styles.overline}>Available for Work </span>
          <h1 style={styles.title}>
            Smart Digital <br/> 
            Solutions for Your <br/>
            <span className="gradient-text">Business [cite: 52, 53, 54]</span>
          </h1>
          <p style={styles.subtitle}>
            We build high-performing websites and mobile apps that help your business scale faster in the digital world[cite: 55].
          </p>
          <div style={{ display: 'flex', gap: '20px' }}>
            <motion.button 
              whileHover={{ scale: 1.05 }}
              onClick={onContactClick}
              className="btn"
              style={styles.heroBtn}
            >
              Contact us [cite: 64]
            </motion.button>
          </div>
        </motion.div>

        <motion.div style={styles.imageSide}>
          {/* PLACE IMAGE HERE */}
          <div style={styles.imagePlaceholder}>
            <div className="floating-blob"></div>
            {/* <img src="YOUR_IMAGE_URL" alt="Hero Illustration" style={{width: '100%'}} /> */}
          </div>
        </motion.div>
      </div>
    </section>
  );
};

const styles = {
  hero: { minHeight: '100vh', display: 'flex', alignItems: 'center', background: '#0a0a0a', paddingTop: '100px' },
  flex: { display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap' },
  textSide: { flex: 1.2, minWidth: '300px' },
  overline: { color: '#2ecc71', fontWeight: 'bold', fontSize: '0.9rem', marginBottom: '10px', display: 'block' },
  title: { fontSize: '4.5rem', fontWeight: '900', lineHeight: '1.1', color: '#fff', marginBottom: '25px' },
  subtitle: { fontSize: '1.1rem', color: '#aaa', marginBottom: '40px', maxWidth: '550px' },
  heroBtn: { background: '#2ecc71', color: '#000', padding: '16px 40px', borderRadius: '50px', fontWeight: 'bold' },
  imageSide: { flex: 1, position: 'relative' },
  imagePlaceholder: { width: '100%', height: '500px', display: 'flex', alignItems: 'center', justifyContent: 'center', border: '1px dashed #333', borderRadius: '20px' }
};

export default Hero;
