import React, { useEffect, useState, useRef } from 'react';
import { useInView, motion, useSpring, useTransform } from 'framer-motion';

function Counter({ value }) {
  const ref = useRef(null);
  const inView = useInView(ref, { once: true });
  const spring = useSpring(0, { stiffness: 50, damping: 20 });
  const display = useTransform(spring, (current) => Math.round(current));
  useEffect(() => { if (inView) spring.set(value); }, [inView, value]);
  return <motion.span ref={ref}>{display}</motion.span>;
}

const Stats = () => {
  return (
    <section style={{ textAlign: 'center' }}>
      <div className="container">
        <span style={{ color: '#2ecc71', fontWeight: 'bold' }}>Company Statistics</span>
        <h2 style={{ fontSize: '2.5rem', margin: '15px 0 60px' }}>Why People say us best <br/> framing company in the worlds [cite: 88]</h2>
        <div style={styles.grid}>
          <div style={styles.item}>
            <h3 style={styles.num}><Counter value={150} />+</h3>
            <p style={styles.label}>Projects Successfully Delivered [cite: 91]</p>
          </div>
          <div style={styles.item}>
            <h3 style={styles.num}><Counter value={85} />+</h3>
            <p style={styles.label}>Skilled Developers & Designers [cite: 91]</p>
          </div>
          <div style={styles.item}>
            <h3 style={styles.num}><Counter value={10} />+</h3>
            <p style={styles.label}>Years of Technical Excellence [cite: 93]</p>
          </div>

        </div>
      </div>
    </section>
  );
};

const styles = {
  grid: { display: 'flex', justifyContent: 'space-around', flexWrap: 'wrap', gap: '40px' },
  item: { flex: 1, minWidth: '200px' },
  num: { fontSize: '4rem', color: '#2ecc71', fontWeight: '900' },
  label: { color: '#aaa', marginTop: '10px' }
};

export default Stats;
