import React from 'react';
import { motion } from 'framer-motion';

const services = [
  { title: "Web Development", icon: "ðŸŒ", desc: "Innovative software solutions tailored to move your business forward." },
  { title: "App Development", icon: "ðŸ“±", desc: "Native and cross-platform mobile solutions for all devices." },
  { title: "UI/UX Design", icon: "ðŸŽ¨", desc: "Clean design that turns complex ideas into seamless solutions." },
  { title: "Product Strategy", icon: "ðŸ“ˆ", desc: "Strategic planning to help your business scale efficiently." },
  { title: "Custom Software", icon: "âš™ï¸", desc: "Tailored software built for your specific business needs." },
  { title: "Post-Launch Support", icon: "ðŸ› ï¸", desc: "Ensuring your digital products stay efficient and updated." }
];

const Services = () => {
  return (
    <section id="services" style={{ background: '#fcfdfd', overflow: 'hidden' }}>
      <div className="container">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.6 }}
          style={{ textAlign: 'center' }}
        >
          <h2 style={{ fontSize: '2.8rem' }}>Our Services</h2>
          <p className="section-subtitle">
            Innovative software solutions tailored to move your business forward.
          </p>
        </motion.div>

        <div style={styles.grid}>
          {services.map((service, index) => (
            <motion.div
              key={index}
              style={styles.card}
              initial={{ opacity: 0, y: 50 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              whileHover={{ 
                y: -15, 
                backgroundColor: "#ffffff",
                boxShadow: "0 20px 40px rgba(0,0,0,0.1)" 
              }}
            >
              <div style={styles.iconBox}>{service.icon}</div>
              <h3 style={styles.cardTitle}>{service.title}</h3>
              <p style={styles.cardDesc}>{service.desc}</p>
              <motion.div 
                style={styles.underline}
                whileHover={{ width: '100%' }}
              />
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
};

const styles = {
  grid: { 
    display: 'grid', 
    gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))', 
    gap: '30px', 
    marginTop: '50px' 
  },
  card: { 
    background: '#fff', 
    padding: '40px 30px', 
    borderRadius: '20px', 
    border: '1px solid #eee',
    position: 'relative',
    cursor: 'pointer'
  },
  iconBox: { fontSize: '2.5rem', marginBottom: '20px' },
  cardTitle: { fontSize: '1.4rem', marginBottom: '15px', color: '#2c3e50' },
  cardDesc: { color: '#777', fontSize: '0.95rem', lineHeight: '1.6' },
  underline: { 
    position: 'absolute', 
    bottom: 0, 
    left: 0, 
    height: '4px', 
    width: '0%', 
    background: '#2ecc71',
    transition: '0.3s'
  }
};

export default Services;
