import React from 'react';
import { motion } from 'framer-motion';

const Hero = () => {
  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: { staggerChildren: 0.3, delayChildren: 0.2 }
    }
  };

  const itemVariants = {
    hidden: { y: 20, opacity: 0 },
    visible: { y: 0, opacity: 1, transition: { duration: 0.8, ease: "easeOut" } }
  };

  return (
    <section id="home" className="container hero-section" style={styles.hero}>
      <motion.div 
        style={styles.content}
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        <motion.h1 variants={itemVariants} style={styles.title}>
          Smart Digital <br />
          <span style={{ color: '#2ecc71' }}>Solutions</span> for <br />
          Your Business
        </motion.h1>
        
        <motion.p variants={itemVariants} style={styles.text}>
          We build high-performing websites and mobile apps that help 
          your business scale faster in the digital world.
        </motion.p>
        
        <motion.div variants={itemVariants}>
          <button className="btn" style={styles.mainBtn}>Get Started</button>
        </motion.div>
      </motion.div>

      <motion.div 
        style={styles.imageWrapper}
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 1, ease: "easeOut" }}
      >
        {/* Floating Animation for the Graphic */}
        <motion.div
          animate={{ y: [0, -20, 0] }}
          transition={{ duration: 4, repeat: Infinity, ease: "easeInOut" }}
          style={styles.graphicBox}
        >
          <div style={styles.circleBg}></div>
          <span style={{ fontSize: '120px' }}>ðŸš€</span>
        </motion.div>
      </motion.div>
    </section>
  );
};

const styles = {
  hero: { display: 'flex', alignItems: 'center', minHeight: '90vh', gap: '40px', padding: '100px 20px' },
  content: { flex: 1.2 },
  title: { fontSize: '4rem', lineHeight: '1.1', marginBottom: '25px', color: '#2c3e50' },
  text: { fontSize: '1.2rem', color: '#666', marginBottom: '40px', maxWidth: '500px' },
  mainBtn: { padding: '15px 40px', fontSize: '1.1rem', boxShadow: '0 10px 20px rgba(46, 204, 113, 0.3)' },
  imageWrapper: { flex: 1, display: 'flex', justifyContent: 'center', position: 'relative' },
  graphicBox: { width: '100%', height: '400px', display: 'flex', alignItems: 'center', justifyContent: 'center', zIndex: 2 },
  circleBg: { position: 'absolute', width: '300px', height: '300px', background: '#eefcf5', borderRadius: '50%', zIndex: -1 }
};

export default Hero;
